mapper.select:
select id, name, status, del, creator, create_time, editor, edit_time from dqc_monitor_baseline where del=0
and name like concat('%',#{name},'%')
and status = #{status}
order by edit_time desc limit #{startIndex}, #{pageSize}

select count(1) from dqc_monitor_baseline where del=0
and name like concat('%',#{name},'%')
and status = #{status}

select id,status,name from dqc_monitor_baseline
where id in(select  baseline_id from dqc_monitor_rule where template_id=#{templateId})
and status=#{status}
