mapper.select:
select
  id, baseline_id, table_name, `partition_expr`
from dqc_monitor_table
where del=0 and baseline_id=#{baselineId}

    and table_name = #{tableName}

select
  id, table_name, latest_alarm_level, access_time,  creator, create_time, editor, edit_time
from dqc_monitor_table
where del = 0

    and baseline_id = #{query.baselineId}


    and table_name like concat('%',#{query.tableName},'%')


    and latest_alarm_level = #{query.latestAlarmLevel}

order by edit_time desc
limit #{query.startIndex}, #{query.pageSize}

select count(1)
from dqc_monitor_table
where del = 0

    and baseline_id = #{query.baselineId}


    and table_name like concat('%',#{query.tableName},'%')


    and latest_alarm_level = #{query.latestAlarmLevel}

mapper.update:
update dqc_monitor_table
set del=1,editor = #{editor},edit_time = now()
where baseline_id != -1 and baseline_id=#{baselineId}

mapper.select:
select baseline_id baselineId,count(*) tableCount from dqc_monitor_table where del=0 and baseline_id in
#{item}
group by baseline_id
